import { collection, deleteDoc, doc, getDocs } from 'firebase/firestore'
import React, { useEffect, useState } from 'react'
import {BrowserRouter, BrowserRouter as Link, Route, Router } from 'react-router-dom'
import { db } from '../firebase'
import Add from './Add'
import './Home.css'

const Home = () => {
  const [posts, setPosts]= useState([])
  const usersCollectionRef= collection(db, "posts")


    useEffect(()=>{
        const getUsers= async ()=>{
          const data= await getDocs(usersCollectionRef)
          setPosts(data.docs.map((doc)=> ({...doc.data(), id: doc.id})))
        }
        getUsers()
    }, [])

    const add = () => {
        console.log('add')
        return <Add />
    }

    const deletePost= async(id)=>{
        const userDoc= doc(db, "posts", id)
        await deleteDoc(userDoc)
      }    

    return(
        <div className='home'>
            <h1>Posts</h1>
            {posts.map((post) => {
                return (
                    <div className='body' key={post.id}>
                        <div className='post_header'>
                            <h3>{post.Title}</h3>
                            <div className='icons'>  
                                <i onClick={()=>deletePost(post.id)} style={{ color: 'darkred', paddingRight: '5px', cursor: 'pointer'}} className='trash alternate outline icon'></i>
                                <i style={{cursor: 'pointer'}} className='edit outline icon'></i>
                            </div>
                        </div>
                        <p>{post.Post}</p>
                        <hr />
                    </div>
                )
              })}
              {/* <BrowserRouter>
                <Route path='/add' component={<Add />} />
                <Link to="/add">
                    <button className='add'>Add</button>
                </Link>
            </BrowserRouter> */}
            <button onClick={add}>Add post</button>
        </div>
    )
}

export default Home
