import { useEffect, useState } from 'react';
import './App.css';
import { db } from './firebase';
import { addDoc, collection, getDocs } from 'firebase/firestore';
import DeleteOutlinedIcon from '@mui/icons-material/DeleteOutlined';
import ModeEditOutlineOutlinedIcon from '@mui/icons-material/ModeEditOutlineOutlined';

function App() {
  const [posts, setPosts]= useState([])
  const usersCollectionRef= collection(db, "posts")
  const [newTitle, setNewTitle]= useState("")
  const [newPost, setNewPost]= useState("")


  const createPost = async ()=>{
    await addDoc(usersCollectionRef, {Title: newTitle, Post: newPost})
  }

  useEffect(()=>{
      const getUsers= async ()=>{
        const data= await getDocs(usersCollectionRef)
        setPosts(data.docs.map((doc)=> ({...doc.data(), id: doc.id })))
      }
      getUsers()
  }, [])

  return (
    <div className="App">
      <div className='header'>
        <form className='ui form'>
          <input className="ui huge focus input" placeholder='Title...' type='text' onChange={ e => setNewTitle(e.target.value)}/>
          <textarea className="ui focus input" placeholder='Post...' type='text' onChange={e => setNewPost(e.target.value)} />
          <button style={{alignSelf: 'center'}} className="ui positive button" onClick={createPost}>Post</button>
        </form>
      </div>
      {console.log(posts)}
      <hr/>
      <h1 className='heading'>Posts</h1>
      <div className='post'>
        {posts.map((post) => {
          return (
            <div key={post.id}>
              <div className='post_header'>
                <h3>{post.Title}</h3>
                <DeleteOutlinedIcon />
                <ModeEditOutlineOutlinedIcon />
              </div>
              <p>{post.Post}</p>
              <hr />
            </div>
          )
        })}
      </div>
    </div>
  );
}

export default App;
